import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './image_upload.css';

const ImageUpload = () => {
  const [selectedImages, setSelectedImages] = useState([]);
  const [uploadedImages, setUploadedImages] = useState([]);
  const [uploadStatus, setUploadStatus] = useState('');

  const handleImageChange = (event) => {
    setSelectedImages([...event.target.files]);
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    for (const image of selectedImages) {
      formData.append('images', image);
    }

    try {
      await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setUploadStatus('Images uploaded successfully!');
      setSelectedImages([]); // Clear selected images
      fetchImages(); // Fetch the newly uploaded images
    } catch (error) {
      console.error('Error uploading images:', error);
      setUploadStatus('Error uploading images: ' + error.message);
    }
  };

  const fetchImages = async () => {
    try {
      const response = await axios.get('/get_images');
      setUploadedImages(response.data);
    } catch (error) {
      console.error('Error fetching images:', error);
    }
  };

  useEffect(() => {
    fetchImages();
  }, []);

  return (
    <div className='image_upload'>
      <div className='circlier_number'>2</div>
      
      <form onSubmit={handleFormSubmit}>
        <input type='file' multiple onChange={handleImageChange} name='image_input' />
        <button type='submit'>Upload Images</button>
      </form>

      {uploadStatus && <p>{uploadStatus}</p>}

      <h2>Uploaded Images:</h2>
      <ul>
        {uploadedImages.map((imagePath, index) => (
          <li key={index}><img src={imagePath} alt={`Image ${index}`} /></li>
        ))}
      </ul>
    </div>
  );
};

export default ImageUpload;
