import React, { useState, useEffect, useRef } from 'react';
import axios from "axios";
import './image_upload.css'

const Image_upload = () => {
  
  // flask intergration starts
  const [selectedImage, setSelectedImage] = useState(null);
  const [uploadedImages, setUploadedImages] = useState([]);
  const [uploadStatus, setUploadStatus] = useState('');
  const fileInputRef = useRef(); // Make sure there's no initial value

  const handleImageChange = (event) => {
    setSelectedImage(event.target.files[0]);
    fileInputRef.current.form.submit(); // Submit the form programmatically
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    
    const formData = new FormData();
    formData.append('image', selectedImage);

    try {
      await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log('Image uploaded successfully!');
      setSelectedImage(null);
      fetchImages();
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  const fetchImages = async () => {
    try {
      const response = await axios.get('/get_images');
      setUploadedImages(response.data);
    } catch (error) {
      console.error('Error fetching images:', error);
    }
  };

  useEffect(() => {
    fetchImages();
  }, []);
  // flask intergration ends

  return (
    <div className='image_upload'>
      <div className='circlier_number'>2</div>
      
      <form onSubmit={handleFormSubmit} ref={fileInputRef}>
        <input type="file" onChange={handleImageChange} />
        {/* Use a hidden button to trigger form submission */}
        <button type="submit" style={{ display: 'none' }}>Upload Image</button>
      </form>

      {uploadStatus && <p>{uploadStatus}</p>}

      <h2>Uploaded Images:</h2>
      <ul>
        {uploadedImages.map((imagePath, index) => (
          <li key={index}><img src={imagePath} alt={`Image ${index}`} /></li>
        ))}
      </ul>
    </div>
  )
}

export default Image_upload
